import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Cuota } from 'src/app/domain/cuota';
import { Solicitud } from 'src/app/domain/solicitud';
import { SolicitudService } from 'src/app/services/solicitud.service';

@Component({
  selector: 'app-solicitud',
  templateUrl: './solicitud.component.html',
  styleUrls: ['./solicitud.component.css']
})
export class SolicitudComponent {
  signUpForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private solicitudServices: SolicitudService){
    this.signUpForm = this.formBuilder.group({
      cedula: ['', Validators.required],
      nombre: ['', Validators.required],
      correo: ['', Validators.required],
      monto: [0, [Validators.required, Validators.min(0)]],
      meses: [0, [Validators.required, Validators.min(0)]]
    });
  }

  onSubmit() {
    if (this.signUpForm.valid) {
      const solicitud: Solicitud = this.signUpForm.value;
      this.solicitudServices.agregarSolicitud(solicitud).subscribe(
        (cuotas: Cuota[]) => { // Ajusta el tipo de respuesta esperada a Cuota[]
          console.log('Registro exitoso', cuotas);
          // Aquí puedes hacer algo con la lista de cuotas, como mostrarla en la UI
        },
        error => {
          console.error('Error al registrar el cliente', error);
          // Aquí manejas errores, como mostrar un mensaje al usuario
        }
      );
    }
  }

}
<div class="signup-container">
    <h2>Solicitud</h2>
    <form [formGroup]="signUpForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="cedula">Cedula:</label>
        <input id="cedula" formControlName="cedula" type="text" placeholder="Cedula" required>
      </div>
      <div class="form-group">
        <label for="nombre">Nombre:</label>
        <input id="nombre" formControlName="nombre" type="text" placeholder="Nombre" required>
      </div>
      <div class="form-group">
        <label for="correo">Correo:</label>
        <input id="correo" formControlName="correo" type="text" placeholder="Correo" required>
      </div>
      <div class="form-group">
        <label for="monto">Monto:</label>
        <input id="monto" formControlName="monto" type="number" placeholder="Monto" required>
      </div>
      <div class="form-group">
        <label for="meses">Meses:</label>
        <input id="meses" formControlName="meses" type="number" placeholder="Meses" required>
      </div>
      <div class="form-actions">
        <button type="submit" [disabled]="!signUpForm.valid">Registrarse</button>
      </div>
    </form>
  </div>

  <div *ngIf="cuotas.length > 0">
  <h2>Lista de Cuotas</h2>
  <ul>
    <li *ngFor="let cuota of cuotas">
      Monto: {{ cuota.monto }}, Fecha de Pago: {{ cuota.fechaPago | date: 'mediumDate' }}
      <!-- Utiliza otros campos de tu objeto Cuota según sea necesario -->
    </li>
  </ul>
</div>
